INSERT INTO `news` (`id`, `title`, `image`, `description`, `content`, `link`, `password`, `created_at`, `updated_at`) VALUES
(28, 'Mô Hình Trả Lương 3P', '20160621180930-315142.jpg', '<p><span style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''times new roman'', times, serif; background: transparent;"><span class="color_2" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; color: rgb(0, 0, 0); background: transparent;"></span></span><span style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''times new roman'', times, serif; background: transparent;"><span class="color_2" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; color: rgb(0, 0, 0); background: transparent;">Ngày\r\n nay, khi xu hướng thị trường phát triển mạnh như vũ bão và sự cạnh \r\ntranh trở nên khốc liệt đòi hỏi các doanh nghiệp luôn luôn phải linh \r\nhoạt để đưa ra những chính sách tiền lương hấp dẫn thu hút được nhiều \r\nnhân tài phục vụ cho nhu cầu của chính họ...</span></span></p>', '<p><span style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''times new roman'', times, serif; background: transparent;"><span class="color_2" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; color: rgb(0, 0, 0); background: transparent;">Ngày nay, khi xu hướng thị trường phát triển mạnh như vũ bão và sự cạnh tranh trở nên khốc liệt đòi hỏi các doanh nghiệp luôn luôn phải linh hoạt để đưa ra những chính sách tiền lương hấp dẫn thu hút được nhiều nhân tài phục vụ cho nhu cầu của chính họ…Vậy, làm thế nào để có thể tự xây dựng được những chính sách tiền lương linh động, hấp dẫn và phù hợp với doanh nghiệp? Đây chính là những bài toán khó những câu hỏi chưa có lời giải đáp cho nhiều nhà làm công tác quản lý nhân sự.</span></span></p><p><span style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-family: ''times new roman'', times, serif; background: transparent;"><span class="color_2" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; color: rgb(0, 0, 0); background: transparent;">Ngân Hàng Ứng Viên&nbsp;sẽ chia sẻ với các bạn một vài yếu tố cơ bản trong công tác xây dựng chính sách tiền lương dựa theo nguyên tắc trả lương 3P (Position, Person và Performance).</span></span></p>', 'http://www.mediafire.com/download/682ytmodetb0uc5/traluong3p.pdf', 'nganhangungvien.com', '2016-06-18 15:41:52', '2016-06-21 11:09:30'),
(29, 'Kỹ Năng Tuyển Dụng Chuyên Nghiệp', '20160621180909-24717.jpg', '<p><span class="color_2" style="font-family: Arial;">Hầu\r\n hết, doanh nghiệp và nhà tuyển dụng luôn đau đầu với bài toán:&nbsp;Làm thế \r\nnào để tuyển được đúng người vào đúng vị trí, theo đúng tiêu chuẩn yêu \r\ncầu của doanh nghiệp?&nbsp;Giải được bài toán này, doanh nghiệp không những \r\nkhông tốn nhiều thời gian, chi phí (bao gồm: phí tuyển dụng, phí trả \r\nlương thử việc, phí đào tạo,…</span></p>', '<p><span style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent;"><span class="color_2" style="margin: 0px; padding: 0px; border: 0px; border-image-source: initial; border-image-slice: initial; border-image-width: initial; border-image-outset: initial; border-image-repeat: initial; outline: 0px; vertical-align: baseline; color: rgb(0, 0, 0); background-image: initial; background-attachment: initial; background-color: transparent; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial; font-family: Arial;">Hầu hết, doanh nghiệp và nhà tuyển dụng luôn đau đầu với bài toán:&nbsp;Làm thế nào để tuyển được đúng người vào đúng vị trí, theo đúng tiêu chuẩn yêu cầu của doanh nghiệp?&nbsp;Giải được bài toán này, doanh nghiệp không những không tốn nhiều thời gian, chi phí (bao gồm: phí tuyển dụng, phí trả lương thử việc, phí đào tạo,… cho các vị trí tuyển không phù hợp) và rất nhiều nguồn lực khác, mà còn nâng cao được sức mạnh của đội ngũ thông qua việc phát huy sở trường và hạn chế.</span></span></p><p><span style="font-family: Arial;"><span style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent;"><span class="color_2" style="margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; color: rgb(0, 0, 0); background: transparent;">Ngân Hàng Ứng Viên sẽ chia sẻ Tài Liệu Kỹ Năng Tuyển Dụng của giảng viên Ngô Qúy Nhâm được trình bày dưới dạng File Pdf.</span></span> <br></span></p><p><br></p>', 'http://www.mediafire.com/download/682ytmodetb0uc5/kynangtuyendung_nhamnq.pdf', 'nganhangungvien.com', '2016-06-18 16:37:00', '2016-07-03 05:23:03');
